#!/usr/bin/env node

const config = require('./config');
const fs = require('fs');
const { runCommand } = require('./commander');

if (!fs.existsSync(config.path)) {
  console.log('Downloading ktlintâ€¦');
  runCommand(
    `curl -sSLO https://github.com/shyiko/ktlint/releases/download/0.29.0/ktlint && chmod a+x ktlint && mkdir -p ~/.ktlint && mv -f ktlint ${
      config.path
    }`
  );
}
